  //         setTripleNext(false);
  //     } else if (btn === "Triple") {
  //         setTripleNext(!tripleNext);
  //         setDoubleNext(false);
  //     } else {
  //         if (doubleNext && typeof btn === "number") {
  //             props.handleClick(btn * 2);
  //             setDoubleNext(false);
  //         } else if (tripleNext && typeof btn === "number") {
  //             props.handleClick(btn * 3);
  //             setTripleNext(false);
  //         } else {
  //             props.handleClick(btn);
  //         }
  //     }
  // };

  return (
    <div className="keyboard">
      {btnValues.flat().map((btn, i) => (
        <NumberButton
          handleClick={() => handleButtonClick(btn)}
          value={btn}
          key={i}
          isActive={
            (doubleNext && btn === "Double") || (tripleNext && btn === "Triple")
          }
          disabled={
            (tripleNext && btn === 25) ||
            (tripleNext && btn === 0) ||
            (doubleNext && btn === 0)
          }
        />
      ))}
    </div>
  );
}

export default Keyboard;
