import clsx from "clsx";
import PodiumPlayerCard from "../PodiumPlayerCard/PodiumPlayerCard";
import "./Podium.css";
import "../PodiumPlayerCard/PodiumPlayerCard.css";

type PlayerProps = {
    name
}
type Props = {
  name?: string;
  userMap?: any;
  list?: any;
};

function Podium({ ...props }: Props) {
  return (
    <div className="podium">
      {props.userMap.map((item: BASIC.PlayerProps, index: number) => {
        const completedRounds =
          item.rounds[item.rounds.length - 1].throw1 === undefined
            ? item.rounds.length - 1
            : item.rounds.length;

        const average =
          completedRounds === 0
            ? 0
            : Math.round((301 - item.score) / completedRounds);
        const playerCardClass = clsx("podiumplayerCard", {
          first: index === 0,
          second: index === 1,
          third: index === 2,
          hide: props.list?.length === 2 && index === 2,
        });
        return (
          <PodiumPlayerCard
            key={item.name + index}
            className={playerCardClass}
            rounds={completedRounds}
            name={item.name}
            placement={`${index + 1}.`}
            averagePerRound={average}
          />
        );
      })}
    </div>
  );
}
export default Podium;
